function AccessButton({show}) {
        if (show) {
            return ( <OpenedDropdown /> )
        }
        
        return ( <ClosedDropdown /> )
        

}

class PrivacyButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            show: false
        };

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState((prevState) => ({ show: !prevState.show }));
    }

  render() {
    const { show } = this.state;

    return (
        <button onClick={this.handleClick} type="button" tabIndex={0} aria-label="Click me">
            <AccessButton show={show} />
        </button>
    );
  }
}

export default PrivacyButton;